new_store = """  Future<bool> _storeSavedComparison(SavedComparison comparison) async {\n    try {\n      final comparisonJson = jsonEncode(comparison.toMap());\n      final success =\n          await ComparisonStorage.addSavedComparison(comparisonJson);\n\n      if (!success) {\n        throw Exception('Failed to persist saved comparison');\n      }\n\n      return true;\n    } catch (e) {\n      // Handle storage error\n      print('Error saving comparison: ');\n      if (context.mounted) {\n        ScaffoldMessenger.of(context).showSnackBar(\n          SnackBar(\n            content: Text('Failed to save comparison: '),\n            backgroundColor: Colors.red,\n          ),\n        );\n      }\n      return false;\n    }\n  }\n"""
